import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [file, setFile] = useState(null);
  const [stats, setStats] = useState(null);
  const [history, setHistory] = useState([]); // State for the history table
  const [loading, setLoading] = useState(false);

  // Function to handle the file upload
  const handleUpload = async () => {
    if (!file) return alert("Please select a file first");
    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/upload/', formData);
      setStats(res.data);
      // After upload, refresh history if your backend supports a history endpoint
      // fetchHistory(); 
    } catch (err) {
      alert("Upload failed. Check your Django backend.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh', fontFamily: 'Segoe UI, sans-serif' }}>
      {/* Header */}
      <nav style={{ backgroundColor: '#1a2a6c', padding: '15px 50px', color: 'white', display: 'flex', justifyContent: 'space-between' }}>
        <h2 style={{ margin: 0 }}>Chemical Equipment Visualizer</h2>
        <span>VIT Bhopal University</span>
      </nav>

      <div style={{ padding: '40px', maxWidth: '1200px', margin: '0 auto' }}>

        {/* Top Section: Upload and Latest Summary */}
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '30px' }}>
          {/* Upload Card */}
          <div style={{ backgroundColor: 'white', padding: '25px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
            <h3 style={{ marginTop: 0 }}>Upload Equipment CSV</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
              <input type="file" onChange={(e) => setFile(e.target.files[0])} style={{ padding: '10px', border: '1px solid #ddd', borderRadius: '5px' }} />
              <button onClick={handleUpload} style={{ padding: '12px', backgroundColor: '#2980b9', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' }}>
                {loading ? 'Analyzing...' : 'Analyze Data'}
              </button>
            </div>
          </div>

          {/* Latest Analysis Summary Card */}
          <div style={{ backgroundColor: 'white', padding: '25px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
            <h3 style={{ marginTop: 0 }}>Latest Analysis Summary</h3>
            {stats ? (
              <div style={{ lineHeight: '1.8' }}>
                <div><b>Total Equipment:</b> {stats.total_count}</div>
                <div><b>Avg Pressure:</b> {stats.avg_pressure?.toFixed(2)}</div>
                <div><b>Avg Temp:</b> {stats.avg_temp?.toFixed(2)}</div>
                <div><b>Avg Flowrate:</b> {stats.avg_flowrate?.toFixed(2)}</div>
              </div>
            ) : (
              <p style={{ color: '#888' }}>Upload a file to see statistics.</p>
            )}
          </div>
        </div>

        {/* Chart Section */}
        {stats && (
          <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)', marginBottom: '30px' }}>
            <h3 style={{ marginTop: 0 }}>Equipment Type Distribution</h3>
            <div style={{ height: '350px' }}>
              <Bar 
                data={{
                  labels: Object.keys(stats.type_distribution),
                  datasets: [{
                    label: 'Unit Count',
                    data: Object.values(stats.type_distribution),
                    backgroundColor: 'rgba(52, 152, 219, 0.7)',
                    borderRadius: 5
                  }]
                }} 
                options={{ maintainAspectRatio: false }}
              />
            </div>
          </div>
        )}

        {/* Upload History Table (Last 5) */}
        <div style={{ backgroundColor: 'white', padding: '25px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)' }}>
          <h3 style={{ marginTop: 0 }}>Upload History (Last 5)</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
            <thead>
              <tr style={{ backgroundColor: '#f2f2f2', textAlign: 'left' }}>
                <th style={{ padding: '12px', borderBottom: '2px solid #ddd' }}>Filename</th>
                <th style={{ padding: '12px', borderBottom: '2px solid #ddd' }}>Date</th>
                <th style={{ padding: '12px', borderBottom: '2px solid #ddd' }}>Total Units</th>
                <th style={{ padding: '12px', borderBottom: '2px solid #ddd' }}>Avg Pressure</th>
              </tr>
            </thead>
            <tbody>
              {/* This mirrors your screenshot's sample row */}
              {stats && (
                <tr>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{file?.name || "sample_equipment_data.csv"}</td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{new Date().toLocaleString()}</td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{stats.total_count}</td>
                  <td style={{ padding: '12px', borderBottom: '1px solid #eee' }}>{stats.avg_pressure?.toFixed(2)}</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

      </div>
    </div>
  );
}

export default App;
