// web-frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [file, setFile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) return alert("Please select a file first");
    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/upload/', formData);
      setStats(res.data);
    } catch (err) {
      alert("Check if Django server is running.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ backgroundColor: '#f4f7f9', minHeight: '100vh', fontFamily: 'Segoe UI, sans-serif' }}>
      <nav style={{ backgroundColor: '#1e2b58', padding: '15px 60px', color: 'white', display: 'flex', justifyContent: 'space-between' }}>
        <h2 style={{ margin: 0 }}>Chemical Equipment Visualizer</h2>
        <span>VIT Bhopal University</span>
      </nav>

      <div style={{ padding: '40px', maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '25px', marginBottom: '30px' }}>
          <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
            <h3>Upload Equipment CSV</h3>
            <input type="file" onChange={(e) => setFile(e.target.files[0])} style={{ margin: '15px 0', display: 'block' }} />
            <button onClick={handleUpload} style={{ width: '100%', padding: '12px', backgroundColor: '#4a90e2', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>
              {loading ? 'Analyzing...' : 'Analyze Data'}
            </button>
          </div>

          <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
            <h3>Latest Analysis Summary</h3>
            {stats && (
              <div style={{ lineHeight: '2' }}>
                <div><strong>Total Equipment:</strong> {stats.total_count}</div>
                <div><strong>Avg Pressure:</strong> {stats.avg_pressure.toFixed(2)}</div>
                <div><strong>Avg Temp:</strong> {stats.avg_temp.toFixed(2)}</div>
                <div><strong>Avg Flowrate:</strong> {stats.avg_flowrate.toFixed(2)}</div>
              </div>
            )}
          </div>
        </div>

        {stats && (
          <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)', marginBottom: '30px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <h3>Equipment Distribution</h3>
              <div style={{ textAlign: 'right' }}>
                <div style={{ color: '#888', fontSize: '12px' }}>TOTAL PROCESSED</div>
                <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1e2b58' }}>{stats.total_count} Units</div>
              </div>
            </div>
            <div style={{ height: '350px' }}>
              <Bar data={{
                labels: Object.keys(stats.type_distribution),
                datasets: [{
                  data: Object.values(stats.type_distribution),
                  backgroundColor: ['#7da1c4', '#8ec0e4', '#a682bc', '#90d59d'],
                  borderRadius: 10
                }]
              }} options={{ maintainAspectRatio: false, plugins: { legend: { display: false } } }} />
            </div>
            <center style={{ marginTop: '20px' }}>
              <a href="http://127.0.0.1:8000/api/report/" target="_blank" rel="noreferrer" style={{ padding: '10px 30px', border: '2px solid #2980b9', borderRadius: '30px', textDecoration: 'none', color: '#2980b9' }}>
                Download PDF Analysis Report â†’
              </a>
            </center>
          </div>
        )}

        <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '15px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
          <h3>Upload History (Last 5)</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
            <thead style={{ backgroundColor: '#fafafa', textAlign: 'left' }}>
              <tr>
                <th style={{ padding: '12px' }}>Filename</th>
                <th style={{ padding: '12px' }}>Date</th>
                <th style={{ padding: '12px' }}>Total Units</th>
                <th style={{ padding: '12px' }}>Avg Pressure</th>
              </tr>
            </thead>
            <tbody>
              {stats && (
                <tr>
                  <td style={{ padding: '12px', borderTop: '1px solid #eee' }}>{file?.name}</td>
                  <td style={{ padding: '12px', borderTop: '1px solid #eee' }}>{new Date().toLocaleString()}</td>
                  <td style={{ padding: '12px', borderTop: '1px solid #eee' }}>{stats.total_count}</td>
                  <td style={{ padding: '12px', borderTop: '1px solid #eee' }}>{stats.avg_pressure.toFixed(2)}</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
export default App;
