import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function App() {
  const [file, setFile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [chartKey, setChartKey] = useState(0);

  const handleUpload = async () => {
    if (!file) return alert("Please select a file first");
    setLoading(true);
    setStats(null); 

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/upload/', formData);
      setStats(res.data);
      setChartKey(prev => prev + 1); 
    } catch (err) {
      alert("Upload failed. Ensure Django is running.");
    } finally {
      setLoading(false);
    }
  };

  const performanceData = stats?.raw_data?.map(item => ({
    ...item,
    score: parseFloat((item['Temperature'] / item['Pressure']).toFixed(2)),
    isGreenZone: item['Temperature'] < 100 && item['Pressure'] < 6
  })) || [];

  const filteredData = stats?.raw_data?.filter(item => 
    item['Equipment Name'].toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  return (
    <div style={{ backgroundColor: '#f4f7f9', minHeight: '100vh', padding: '20px', fontFamily: '"Segoe UI", sans-serif' }}>
      <style>{`
        @keyframes slideInLeft {
          from { opacity: 0; transform: translateX(-30px); }
          to { opacity: 1; transform: translateX(0); }
        }
        .animate-row {
          animation: slideInLeft 0.5s ease-out forwards;
          opacity: 0;
        }
      `}</style>

      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        <div style={headerStyle}>
          <h2 style={{ margin: 0 }}>Chemical Equipment Parameter Visualizer</h2>
          <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
             <input type="file" onChange={(e) => setFile(e.target.files[0])} />
             <button onClick={handleUpload} disabled={loading} style={btnStyle}>
               {loading ? 'Analyzing...' : 'Analyze Data'}
             </button>
          </div>
        </div>

        {stats && (
          <div key={chartKey}> 
            {/* KPI Cards */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '25px' }}>
              <Card title="Total Units" value={stats.total_count} icon="üè≠" />
              <Card title="Avg Pressure" value={`${stats.avg_pressure} bar`} icon="üå°Ô∏è" />
              <Card title="Max Temp" value={`${stats.max_temp} ¬∞C`} icon="üî•" />
              <Card title="Avg Flow" value={`${stats.avg_flowrate} m¬≥/h`} icon="üíß" />
            </div>

            {/* Building Charts */}
            <div style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr', gap: '25px', marginBottom: '25px' }}>
              <div style={panelStyle}>
                <Bar 
                  data={{ 
                    labels: Object.keys(stats.type_distribution), 
                    datasets: [{ label: 'Units', data: Object.values(stats.type_distribution), backgroundColor: '#1a2a6c', borderRadius: 5 }] 
                  }} 
                  options={{
                    animation: { duration: 2000, easing: 'easeOutBounce', delay: (ctx) => ctx.dataIndex * 200 },
                    scales: { y: { beginAtZero: true } },
                    maintainAspectRatio: false
                  }}
                  height={320}
                />
              </div>
              <div style={panelStyle}>
                <Pie 
                  data={{ 
                    labels: Object.keys(stats.type_distribution), 
                    datasets: [{ data: Object.values(stats.type_distribution), backgroundColor: ['#b21f1f', '#fdbb2d', '#22c1c3', '#1a2a6c'] }] 
                  }} 
                  options={{
                    animation: { animateRotate: true, animateScale: true, duration: 2000, easing: 'easeInOutCirc' }
                  }}
                />
              </div>
            </div>

            {/* Log with Sliding Rows */}
            <div style={{ display: 'grid', gridTemplateColumns: '1.8fr 1fr 1fr', gap: '20px', alignItems: 'start' }}>
              <div style={panelStyle}>
                <h3 style={{ margin: '0 0 15px 0' }}>Detailed Equipment Log</h3>
                <div style={{ maxHeight: '350px', overflowY: 'auto' }}>
                  <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                      <tr style={{ textAlign: 'left', borderBottom: '2px solid #edf2f7', color: '#718096', fontSize: '11px' }}>
                        <th style={tdStyle}>NAME</th><th style={tdStyle}>TEMP</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredData.map((row, i) => (
                        <tr 
                          key={i} 
                          className="animate-row" 
                          style={{ borderBottom: '1px solid #f7fafc', animationDelay: `${(i * 0.1) + 1.5}s` }}
                        >
                          <td style={tdStyle}>{row['Equipment Name']}</td>
                          <td style={{ ...tdStyle, color: row['Temperature'] > 115 ? '#e53e3e' : '#2d3748', fontWeight: row['Temperature'] > 115 ? 'bold' : 'normal' }}>
                            {row['Temperature']} ¬∞C
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>

              <div style={{ ...panelStyle, borderTop: '5px solid #fdbb2d' }}>
                <h4>üèÜ Leaderboard</h4>
                {performanceData.sort((a, b) => a.score - b.score).slice(0, 5).map((unit, i) => (
                  <div key={i} className="animate-row" style={{ ...sideCardStyle, animationDelay: `${(i * 0.1) + 2}s` }}>
                    <div style={{ fontWeight: '600', fontSize: '13px' }}>{unit['Equipment Name']}</div>
                    <div style={{ fontSize: '11px', color: '#718096' }}>Score: {unit.score}</div>
                  </div>
                ))}
              </div>

              <div style={{ ...panelStyle, borderTop: '5px solid #1a2a6c' }}>
                <h4>üìÇ History</h4>
                {stats.history.map((h, i) => (
                  <div key={i} className="animate-row" style={{ ...sideCardStyle, backgroundColor: '#f8fafc', animationDelay: `${(i * 0.1) + 2.5}s` }}>
                    <div style={{ fontWeight: 'bold', color: '#1a2a6c', fontSize: '12px' }}>{h.filename}</div>
                    <div style={{ color: '#a0aec0', fontSize: '10px' }}>{h.date}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

const Card = ({ title, value, icon }) => (
  <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #edf2f7', textAlign: 'center' }}>
    <div style={{ fontSize: '24px' }}>{icon}</div>
