import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function App() {
  const [file, setFile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (stats) {
      const timer = setTimeout(() => setIsVisible(true), 100);
      return () => clearTimeout(timer);
    } else {
      setIsVisible(false);
    }
  }, [stats]);

  const handleUpload = async () => {
    if (!file) return alert("Please select a file first");

    setStats(null); // Clear previous state to reset animations
    setIsVisible(false);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/upload/', formData);
      setStats(res.data);
    } catch (err) {
      alert("Upload failed. Ensure Django is running.");
    } finally {
      setLoading(false);
    }
  };

  const performanceData = stats?.raw_data?.map(item => {
    const score = (item['Temperature'] / item['Pressure']).toFixed(2);
    const isGreenZone = item['Temperature'] < 100 && item['Pressure'] < 6;
    return { ...item, score: parseFloat(score), isGreenZone };
  }) || [];

  const bestPerformer = performanceData.length > 0 
    ? [...performanceData].sort((a, b) => a.score - b.score)[0] 
    : null;

  const filteredData = stats?.raw_data?.filter(item => 
    item['Equipment Name'].toLowerCase().includes(searchTerm.toLowerCase()) ||
    item['Type']?.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  return (
    <div style={{ backgroundColor: '#f4f7f9', minHeight: '100vh', padding: '20px', fontFamily: '"Segoe UI", sans-serif' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>

        <div style={{ 
          display: 'flex', justifyContent: 'space-between', alignItems: 'center', 
          backgroundColor: '#1a2a6c', padding: '15px 30px', borderRadius: '12px', 
          color: 'white', marginBottom: '25px', boxShadow: '0 4px 15px rgba(26, 42, 108, 0.2)'
        }}>
          <h2 style={{ margin: 0 }}>Chemical Equipment Parameter Visualizer</h2>
          <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
             <input type="file" onChange={(e) => setFile(e.target.files[0])} style={{ fontSize: '12px' }} />
             <button onClick={handleUpload} disabled={loading} style={btnStyle}>
               {loading ? 'Processing...' : 'Analyze Data'}
             </button>
          </div>
        </div>

        {stats && (
          <div style={{ 
            opacity: isVisible ? 1 : 0, 
            transform: isVisible ? 'translateY(0)' : 'translateY(20px)', 
            transition: 'opacity 0.6s ease-out, transform 0.6s ease-out' 
          }}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '25px' }}>
              <Card title="Total Units" value={stats.total_count} icon="üè≠" />
              <Card title="Avg Pressure" value={`${stats.avg_pressure} bar`} icon="üå°Ô∏è" />
              <Card title="Max Temp" value={`${stats.max_temp} ¬∞C`} icon="üî•" />
              <Card title="Avg Flow" value={`${stats.avg_flowrate} m¬≥/h`} icon="üíß" />
            </div>

            {bestPerformer && (
              <div style={{ 
                backgroundColor: '#ffffff', borderLeft: '6px solid #38b2ac', padding: '18px', 
                borderRadius: '10px', marginBottom: '25px', display: 'flex', alignItems: 'center', 
                gap: '20px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)'
              }}>
                <span style={{ fontSize: '30px' }}>üèÜ</span>
                <div>
                  <h4 style={{ margin: 0, color: '#2c7a7b' }}>Optimization Lead: {bestPerformer['Equipment Name']}</h4>
                  <p style={{ margin: '4px 0 0 0', color: '#718096', fontSize: '14px' }}>Efficiency Metric: <b>{bestPerformer.score}</b></p>
                </div>
              </div>
            )}

            <div style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr', gap: '25px', marginBottom: '25px' }}>
              <div style={panelStyle}>
                <Bar 
                  key={`bar-${stats.total_count}-${stats.avg_pressure}`} // Forces build-up animation
                  data={{ 
                    labels: Object.keys(stats.type_distribution), 
                    datasets: [{ 
                      label: 'Units', 
                      data: Object.values(stats.type_distribution), 
                      backgroundColor: '#1a2a6c', 
                      borderRadius: 5 
                    }] 
                  }} 
                  options={{
                    animation: { duration: 2000, easing: 'easeOutQuart' }, // Bars rise from X-axis
                    scales: { y: { beginAtZero: true } }
                  }}
                />
              </div>
              <div style={panelStyle}>
                <Pie 
                  key={`pie-${stats.total_count}-${stats.avg_pressure}`} // Forces clockwise sweep
                  data={{ 
                    labels: Object.keys(stats.type_distribution), 
                    datasets: [{ 
                      data: Object.values(stats.type_distribution), 
                      backgroundColor: ['#b21f1f', '#fdbb2d', '#22c1c3', '#1a2a6c', '#4a5568'] 
                    }] 
                  }} 
                  options={{
                    animation: { 
                      animateRotate: true, // Clockwise circular draw
                      animateScale: true, 
                      duration: 2500, 
                      easing: 'easeInOutCirc' 
                    }
                  }}
                />
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1.8fr 1fr 1fr', gap: '20px', alignItems: 'start' }}>
              <div style={panelStyle}>
                <h3 style={{ margin: '0 0 15px 0', fontSize: '18px' }}>Detailed Equipment Log</h3>
                <div style={{ maxHeight: '350px', overflowY: 'auto' }}>
                  <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead>
                      <tr style={{ textAlign: 'left', borderBottom: '2px solid #edf2f7', color: '#718096', fontSize: '11px' }}>
                        <th style={tdStyle}>NAME</th><th style={tdStyle}>TEMP</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredData.map((row, i) => (
                        <tr key={i} style={{ borderBottom: '1px solid #f7fafc' }}>
                          <td style={tdStyle}>{row['Equipment Name']}</td>
                          <td style={{ ...tdStyle, color: row['Temperature'] > 115 ? '#e53e3e' : '#2d3748', fontWeight: row['Temperature'] > 115 ? 'bold' : 'normal' }}>
                            {row['Temperature']} ¬∞C
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>

              <div style={{ ...panelStyle, borderTop: '5px solid #fdbb2d' }}>
                <h4 style={{ margin: '0 0 15px 0' }}>üèÜ Leaderboard</h4>
                {performanceData.sort((a, b) => a.score - b.score).map((unit, i) => (
                  <div key={i} style={sideCardStyle}>
                    <div style={{ fontWeight: '600', fontSize: '13px' }}>{unit['Equipment Name']}</div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '11px', color: '#718096' }}>
                      <span>Score: {unit.score}</span>
                      {unit.isGreenZone && <span style={{ color: '#38a169', fontWeight: 'bold' }}>‚óè OPTIMAL</span>}
                    </div>
                  </div>
                ))}
              </div>

              <div style={{ ...panelStyle, borderTop: '5px solid #1a2a6c' }}>
                <h4 style={{ margin: '0 0 15px 0' }}>üìÇ Dataset History</h4>
                {stats.history.map((h, i) => (
                  <div key={i} style={{ ...sideCardStyle, backgroundColor: '#f8fafc' }}>
                    <div style={{ fontWeight: 'bold', color: '#1a2a6c', fontSize: '12px' }}>{h.filename}</div>
                    <div style={{ color: '#a0aec0', fontSize: '10px' }}>{h.date}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

const Card = ({ title, value, icon }) => (
  <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #edf2f7', textAlign: 'center' }}>
    <div style={{ fontSize: '24px' }}>{icon}</div>
    <div style={{ color: '#a0aec0', fontSize: '11px', textTransform: 'uppercase' }}>{title}</div>
    <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#2d3748' }}>{value}</div>
  </div>
);

const btnStyle = { backgroundColor: '#fdbb2d', color: '#1a2a6c', border: 'none', padding: '10px 20px', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' };
const panelStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '15px', border: '1px solid #edf2f7' };
const tdStyle = { padding: '12px 8px', fontSize: '13px' };
const sideCardStyle = { padding: '12px', marginBottom: '8px', borderRadius: '8px', border: '1px solid #f1f5f9' };

export default App;
