import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function App() {
  const [file, setFile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  // Using a random ID ensures charts are destroyed and rebuilt on every upload
  const [renderId, setRenderId] = useState(Math.random()); 

  const handleUpload = async () => {
    if (!file) return alert("Please select a file first");

    setLoading(true);
    setStats(null); // Unmount current dashboard

    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/upload/', formData);
      setStats(res.data);
      // New random ID forces React to treat charts as brand new elements
      setRenderId(Math.random()); 
    } catch (err) {
      alert("Upload failed. Ensure Django is running.");
    } finally {
      setLoading(false);
    }
  };

  const filteredData = stats?.raw_data?.filter(item => 
    item['Equipment Name'].toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  return (
    <div style={{ backgroundColor: '#f4f7f9', minHeight: '100vh', padding: '20px', fontFamily: '"Segoe UI", sans-serif' }}>
      <style>{`
        @keyframes slideIn { from { opacity: 0; transform: translateY(20px); } to { opacity: 1; transform: translateY(0); } }
        .dashboard-container { animation: slideIn 0.8s ease-out forwards; }
      `}</style>

      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', backgroundColor: '#1a2a6c', padding: '20px', borderRadius: '12px', color: 'white', marginBottom: '25px' }}>
          <h2 style={{ margin: 0 }}>Chemical Equipment Parameter Visualizer</h2>
          <div>
             <input type="file" onChange={(e) => setFile(e.target.files[0])} />
             <button onClick={handleUpload} disabled={loading} style={btnStyle}>
               {loading ? 'Analyzing...' : 'Analyze Data'}
             </button>
          </div>
        </div>

        {stats && (
          <div key={renderId} className="dashboard-container"> 
            {/* Summary Cards */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '25px' }}>
              <Card title="Total Units" value={stats.total_count} icon="ðŸ­" />
              <Card title="Avg Pressure" value={`${stats.avg_pressure} bar`} icon="ðŸŒ¡ï¸" />
              <Card title="Max Temp" value={`${stats.max_temp} Â°C`} icon="ðŸ”¥" />
              <Card title="Avg Flow" value={`${stats.avg_flowrate} mÂ³/h`} icon="ðŸ’§" />
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr', gap: '25px', marginBottom: '25px' }}>
              {/* Bar Chart: Rising Sequential Build-up */}
              <div style={panelStyle}>
                <Bar 
                  data={{ 
                    labels: Object.keys(stats.type_distribution), 
                    datasets: [{ label: 'Units', data: Object.values(stats.type_distribution), backgroundColor: '#1a2a6c' }] 
                  }} 
                  options={{
                    animation: {
                      duration: 2000,
                      easing: 'easeOutBounce', // Physical bounce effect
                      delay: (ctx) => ctx.dataIndex * 150 // Sequential build from left to right
                    },
                    scales: { y: { beginAtZero: true } },
                    maintainAspectRatio: false
                  }}
                  height={300}
                />
              </div>

              {/* Pie Chart: Clockwise Sweep */}
              <div style={panelStyle}>
                <Pie 
                  data={{ 
                    labels: Object.keys(stats.type_distribution), 
                    datasets: [{ data: Object.values(stats.type_distribution), backgroundColor: ['#b21f1f', '#fdbb2d', '#22c1c3', '#1a2a6c'] }] 
                  }} 
                  options={{
                    animation: { 
                      animateRotate: true, // Clockwise sweep
                      animateScale: true, 
                      duration: 2500,
                      easing: 'easeInOutCirc'
                    }
                  }}
                />
              </div>
            </div>

            <div style={{ backgroundColor: 'white', padding: '25px', borderRadius: '15px' }}>
              <h3 style={{ marginTop: 0 }}>Detailed Equipment Log</h3>
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ textAlign: 'left', borderBottom: '2px solid #eee' }}>
                    <th>EQUIPMENT NAME</th><th>TEMPERATURE</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.map((row, i) => (
                    <tr key={i} style={{ borderBottom: '1px solid #f9f9f9' }}>
                      <td style={{ padding: '12px' }}>{row['Equipment Name']}</td>
                      <td style={{ color: row['Temperature'] > 115 ? 'red' : 'black' }}>{row['Temperature']} Â°C</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

const Card = ({ title, value, icon }) => (
  <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '12px', textAlign: 'center' }}>
    <div style={{ fontSize: '24px' }}>{icon}</div>
    <div style={{ color: '#888', fontSize: '11px' }}>{title}</div>
    <div style={{ fontSize: '20px', fontWeight: 'bold' }}>{value}</div>
  </div>
);

const btnStyle = { backgroundColor: '#fdbb2d', color: '#1a2a6c', border: 'none', padding: '10px 20px', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' };
const panelStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '15px' };

export default App;
