import React, { useState } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function App() {
  const [file, setFile] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const handleUpload = async () => {
    if (!file) return alert("Please select a file first");
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/upload/', formData);
      setStats(res.data);
    } catch (err) {
      alert("Upload failed. Ensure Django is running on port 8000.");
    } finally {
      setLoading(false);
    }
  };

  // Performance Logic
  const performanceData = stats?.raw_data?.map(item => {
    const score = (item['Temperature'] / item['Pressure']).toFixed(2);
    const isGreenZone = item['Temperature'] < 100 && item['Pressure'] < 6;
    return { ...item, score: parseFloat(score), isGreenZone };
  }) || [];

  const bestPerformer = performanceData.length > 0 
    ? [...performanceData].sort((a, b) => a.score - b.score)[0] 
    : null;

  const filteredData = stats?.raw_data?.filter(item => 
    item['Equipment Name'].toLowerCase().includes(searchTerm.toLowerCase()) ||
    item['Type']?.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  return (
    <div style={{ backgroundColor: '#f4f7f9', minHeight: '100vh', padding: '20px', fontFamily: '"Segoe UI", Roboto, Helvetica, Arial, sans-serif' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>

        {/* Dashboard Header */}
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#1a2a6c', padding: '15px 30px', borderRadius: '12px', color: 'white', marginBottom: '25px', boxShadow: '0 4px 15px rgba(26, 42, 108, 0.2)' }}>
          <h2 style={{ margin: 0, letterSpacing: '0.5px' }}>Chemical Analytics Dashboard</h2>
          <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
             <input type="file" onChange={(e) => setFile(e.target.files[0])} style={{ fontSize: '12px' }} />
             <button onClick={handleUpload} disabled={loading} style={btnStyle}>
               {loading ? 'Processing...' : 'Analyze Data'}
             </button>
          </div>
        </div>

        {stats && (
          <>
            {/* KPI Summary Cards */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '25px' }}>
              <Card title="Total Units" value={stats.total_count} icon="üè≠" />
              <Card title="Avg Pressure" value={`${stats.avg_pressure} bar`} icon="üå°Ô∏è" />
              <Card title="Max Temp" value={`${stats.max_temp} ¬∞C`} icon="üî•" />
              <Card title="Avg Flow" value={`${stats.avg_flowrate} m¬≥/h`} icon="üíß" />
            </div>

            {/* Best Performer Spotlight */}
            {bestPerformer && (
              <div style={{ backgroundColor: '#ffffff', borderLeft: '6px solid #38b2ac', padding: '18px', borderRadius: '10px', marginBottom: '25px', display: 'flex', alignItems: 'center', gap: '20px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' }}>
                <span style={{ fontSize: '30px' }}>üèÜ</span>
                <div>
                  <h4 style={{ margin: 0, color: '#2c7a7b', fontSize: '18px' }}>Optimization Lead: {bestPerformer['Equipment Name']}</h4>
                  <p style={{ margin: '4px 0 0 0', color: '#718096', fontSize: '14px' }}>Efficiency Metric: <b>{bestPerformer.score}</b> (Highest Stability Index)</p>
                </div>
              </div>
            )}

            {/* Visualization Grid */}
            <div style={{ display: 'grid', gridTemplateColumns: '1.5fr 1fr', gap: '25px', marginBottom: '25px' }}>
              <div style={panelStyle}><Bar data={{ labels: Object.keys(stats.type_distribution), datasets: [{ label: 'Units', data: Object.values(stats.type_distribution), backgroundColor: '#1a2a6c', borderRadius: 5 }] }} options={{ plugins: { legend: { display: false } } }} /></div>
              <div style={panelStyle}><Pie data={{ labels: Object.keys(stats.type_distribution), datasets: [{ data: Object.values(stats.type_distribution), backgroundColor: ['#b21f1f', '#fdbb2d', '#22c1c3', '#1a2a6c', '#4a5568'] }] }} /></div>
            </div>

            {/* Restructured Bottom Grid */}
            <div style={{ display: 'grid', gridTemplateColumns: '1.8fr 1fr 1fr', gap: '20px', alignItems: 'start' }}>

              {/* 1. Main Column: Equipment Log */}
              <div style={{ ...panelStyle, minHeight: '480px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                  <h3 style={{ margin: 0, color: '#1a2a6c', fontSize: '18px' }}>Detailed Equipment Log</h3>
                  <input 
                    placeholder="Search equipment..." 
                    style={{ padding: '8px 12px', borderRadius: '6px', border: '1px solid #e2e8f0', width: '200px', outline: 'none' }} 
                    onChange={(e) => setSearchTerm(e.target.value)} 
                  />
                </div>
                <div style={{ maxHeight: '350px', overflowY: 'auto', borderRadius: '4px' }}>
                  <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                    <thead style={{ position: 'sticky', top: 0, backgroundColor: 'white', z_index: 10 }}>
                      <tr style={{ textAlign: 'left', borderBottom: '2px solid #edf2f7', color: '#718096', fontSize: '11px', textTransform: 'uppercase' }}>
                        <th style={tdStyle}>Name</th>
                        <th style={tdStyle}>Current Temp</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredData.map((row, i) => (
                        <tr key={i} style={{ borderBottom: '1px solid #f7fafc' }}>
                          <td style={{ ...tdStyle, fontWeight: '500' }}>{row['Equipment Name']}</td>
                          <td style={{ 
                            ...tdStyle, 
                            color: row['Temperature'] > 115 ? '#e53e3e' : '#2d3748', 
                            fontWeight: row['Temperature'] > 115 ? 'bold' : '500' 
                          }}>
                            {row['Temperature']} ¬∞C
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                <div style={{ marginTop: '20px', borderTop: '1px solid #edf2f7', paddingTop: '15px' }}>
                   <a href="http://127.0.0.1:8000/api/report/" target="_blank" rel="noreferrer" style={reportBtn}>üì• Generate Full PDF Report</a>
                </div>
              </div>

              {/* 2. Sidebar: Leaderboard */}
              <div style={{ ...panelStyle, borderTop: '5px solid #fdbb2d' }}>
                <h4 style={{ margin: '0 0 15px 0', fontSize: '16px' }}>üèÜ Top Performers</h4>
                <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                  {performanceData.sort((a, b) => a.score - b.score).map((unit, i) => (
                    <div key={i} style={sideCardStyle}>
                      <div style={{ fontWeight: '600', fontSize: '13px', marginBottom: '4px' }}>{unit['Equipment Name']}</div>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <span style={{ color: '#718096', fontSize: '11px' }}>Score: <b>{unit.score}</b></span>
                        {unit.isGreenZone && <span style={{ color: '#38a169', fontSize: '10px', fontWeight: 'bold' }}>‚óè OPTIMAL</span>}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* 3. Sidebar: Dataset History */}
              <div style={{ ...panelStyle, borderTop: '5px solid #1a2a6c' }}>
                <h4 style={{ margin: '0 0 15px 0', fontSize: '16px' }}>üìÇ Dataset History</h4>
                <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                  {stats.history.map((h, i) => (
                    <div key={i} style={{ ...sideCardStyle, backgroundColor: '#f8fafc' }}>
                      <div style={{ fontWeight: 'bold', color: '#1a2a6c', fontSize: '12px', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>{h.filename}</div>
                      <div style={{ color: '#a0aec0', fontSize: '10px', margin: '3px 0' }}>{h.date}</div>
                      <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '10px', color: '#4a5568', marginTop: '6px' }}>
                        <span>Units: {h.count}</span>
                        <span>Avg P: {h.avg_p}</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

            </div>
          </>
        )}
      </div>
    </div>
  );
}

const Card = ({ title, value, icon }) => (
  <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.02)', textAlign: 'center', border: '1px solid #edf2f7' }}>
    <div style={{ fontSize: '24px', marginBottom: '8px' }}>{icon}</div>
    <div style={{ color: '#a0aec0', fontSize: '11px', textTransform: 'uppercase', fontWeight: '600', letterSpacing: '0.5px' }}>{title}</div>
    <div style={{ fontSize: '20px', fontWeight: 'bold', color: '#2d3748', marginTop: '4px' }}>{value}</div>
  </div>
);

const btnStyle = { backgroundColor: '#fdbb2d', color: '#1a2a6c', border: 'none', padding: '10px 20px', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer', transition: 'all 0.2s', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' };
const panelStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '15px', boxShadow: '0 4px 12px rgba(0,0,0,0.03)', border: '1px solid #edf2f7' };
const tdStyle = { padding: '12px 8px', fontSize: '14px' };
const sideCardStyle = { padding: '12px', marginBottom: '10px', borderRadius: '8px', border: '1px solid #f1f5f9', backgroundColor: '#fff' };
const reportBtn = { textDecoration: 'none', color: '#1a2a6c', fontWeight: 'bold', fontSize: '13px', border: '2px solid #1a2a6c', padding: '8px 16px', borderRadius: '6px', display: 'inline-block', transition: '0.3s' };

export default App;
